import React from 'react'
import { connect } from 'react-redux'
import Field from '../Sort/Field'
import Range from './Range'

class FilterBox extends React.Component {
  constructor(props) {
    super(props)
    this.onFieldChosen = this.onFieldChosen.bind(this)
    this.onRangeChosen = this.onRangeChosen.bind(this)
    this.state = {
      field: null,
      from: undefined,
      to: undefined
    }
  }

  onFieldChosen(field) {
    this.setState({
      field
    }, () => console.log(this.state))
  }

  onRangeChosen(range) {
    this.setState(range, console.log(this.state))
  }

  render() {
    return (
      <div className="card">
        <div className="card-header">
          <h1 className="card-header-title">Filter</h1>
        </div>
        <div className="card-content">
          <Field onFieldChosen={this.onFieldChosen}/>
          <Range onRangeChosen={this.onRangeRange}/>
          <button className="button is-info is-rounded">Apply</button>
        </div>
      </div>
    )
  }
}

export default FilterBox
