import React from 'react'
import { connect } from 'react-redux'
import { getOneAd } from '../../redux/actions'

class AdPage extends React.Component {
  componentDidMount() {
    const { ad } = this.props
    if (!ad) {

    }
  }
  render() {
    const { ad } = this.props
    return (
      <div className="card">
      <div className="card-header">
        <p className="card-header-title">{ad.title}</p>
      </div>
      <div className="card-content">
        <div>
          <p className="title is-5">Details</p>
          Price: {ad.price}<br/>
          Size: {ad.size}<br/>
          Number of bedrooms: {ad.totalBedrooms}<br />
          Number of floors: {ad.totalFloors}<br />
          Direction: {ad.direction}<br />
          <p className="title is-5">Owner's info</p>
          Owner's name: {ad.contact.name}<br />
          Owner's contact: {ad.contact.phone}<br />
          <p className="title is-5">Location</p>
          {ad.address.street}, {ad.address.district}, {ad.address.city}
          <p className="title is-5">Available until</p>
          {new Date(ad.expDate).toLocaleDateString()}
        </div>
      </div>
    </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    ad: state.advertisements.ads.find(ad => ad._id === ownProps.match.params.id)
  }
}

export default connect(mapStateToProps)(AdPage)
