import axios from 'axios'
import auth from '../sevices/authService'
import history from '../router/history'

function login(username, password) {
  return new Promise((resolve, reject) => {
    axios.post('http://localhost:8080/authenticate', {
      name: username,
      password
    }, {
      headers: {
        "Content-Type": "application/json"
      }
    })
    .then(resp => resp.data)
    .then(data => {
      auth.login(data.data.accessToken)
      return data
    })
    .then(data => resolve(data))
    .catch(error => handleResponse(error, reject))
  })
}

function register(username, password) {
  return new Promise((resolve, reject) => {
    axios.post('http://localhost:8080/users', {
      name: username,
      password
    }, {
      headers: {
        "Content-Type": "application/json"
      }
    })
    .then(resp => { console.log(resp); return resp.data })
    .then(data => {
      auth.login(data.data.accessToken)
      return data
    })
    .then(data => resolve(data))
    .catch(error => { 
      reject(error) 
    })
  })
}

function getAllAdvertisements() {
  return new Promise((resolve, reject) => {
    axios.get('http://localhost:8080/advertisements')
         .then(resp => resp.data)
         .then(data => resolve(data))
         .catch(error => reject(error))
  })
}

function getOwnAdvertisement() {
  return new Promise((resolve, reject) => {
    axios.get('http://localhost:8080/advertisements/me', {
      headers: auth.getAuthHeader()
    })
    .then(resp => resolve(resp.data))
    .catch(error => reject(error))
  })
}

function deleteAd(_id) {
  return new Promise((resolve, reject) => {
    axios.delete('http://localhost:8080/advertisements', {
      headers: auth.getAuthHeader(),
      data: {
        _id
      }
    })
    .then(data => resolve(data.message))
    .catch(error => reject(error))
  })
}

function addAdvertisement(advertisement) {
  return new Promise((resolve, reject) => {
    axios.post('http://localhost:8080/advertisements', advertisement, {
      headers: {
        'Content-Type': 'application/json',
        ...auth.getAuthHeader()
      }
    })
    .then(resp => resolve(resp.data))
    .catch(error => reject(error))
  })
}

function updateAdvertisement(advertisement) {
  return new Promise((resolve, reject) => {
    axios.put('http://localhost:8080/advertisements', advertisement, {
      headers: {
        'Content-Type': 'application/json',
        ...auth.getAuthHeader()
      }
    })
    .then(resp => resolve(resp.data))
    .catch(error => reject(error))
  })
}

function getUserInfo() {
  return new Promise((resolve, reject) => {
    axios.get('http://localhost:8080/users/me', {
      headers: auth.getAuthHeader()
    })
    .then(resp => { 
      resolve(resp.data) 
    })
    .catch(error => handleResponse(error, reject))
  })
}

function logout() {
  auth.logout()
  history.push('/login')
}

function handleResponse(error, reject) {
  const { data, status } = error.response
  console.log(data)
  if (status === 302) {
    logout()
  } else {
    reject(error)
  }
}

export default {
  login,
  logout,
  register,
  getAllAdvertisements,
  getOwnAdvertisement,
  deleteAd,
  addAdvertisement,
  updateAdvertisement,
  getUserInfo
}
