function login(token) {
  localStorage.setItem("accessToken", token)
}

function logout() {
  localStorage.removeItem("accessToken")
}

function getAuthHeader() {
  return {
    'x-access-token': localStorage.getItem("accessToken")
  }
}

export default {
  login,
  logout,
  getAuthHeader
}
