import {
  LOGIN_FAILED,
  LOGIN_START,
  LOGIN_SUCCESS,
  LOGOUT,
  FETCH_USER_SUCCESS,
  FETCH_USER_FAILED
} from '../constants/action-types'
import auth from '../../sevices/authService'

const initialState = auth.isLoggedIn() ? {
  loggingIn: false,
  user: null,
  authFailed: false,
  token: auth.getToken()
} : {
  loggingIn: false,
  user: null,
  authFailed: false,
  token: null
}

function authReducer(state = initialState, action) {
  switch(action.type) {
    case LOGIN_START:
      return {
        loggingIn: true,
        user: null,
        authFailed: false,
        token: null
      }
    case LOGIN_FAILED:
      return {
        loggingIn: false,
        user: null,
        authFailed: true,
        token: null
      }
    case LOGIN_SUCCESS:
      return {
        loggingIn: false,
        user: action.payload.userId, 
        authFailed: false,
        token: action.payload.accessToken
      }
    case LOGOUT:
      return {
        loggingIn: false,
        user: null,
        authFailed: false,
        token: null
      }
    case FETCH_USER_SUCCESS:
      return {
        ...state,
        user: action.payload.data
      }
    case FETCH_USER_FAILED:
      return {
        ...state,
        user: null
      }
    default:
      return state
  }
}

export default authReducer
