import React from 'react'
import { connect } from 'react-redux'

const AdPage = ({ ad }) => {
  return (
    <div className="card">
      <div className="card-header">
        <p className="card-header-title">{ad.title}</p>
      </div>
      <div className="card-content">
        <div>
          <p className="title is-3">Details</p>
          Price: {ad.price}<br/>
          Size: {ad.size}<br/>
          Number of bedrooms: {ad.totalBedrooms}<br />
          Number of floors: {ad.totalFloors}<br />
          Direction: {ad.direction}<br />
          <p className="title is-3">Owner's info</p>
          Owner's name: {ad.contact.name}<br />
          Owner's contact: {ad.contact.phone}<br />
          <p className="title is-3">Location</p>
          {ad.address.street}, {ad.address.district}, {ad.address.city}
          <p className="title is-3">Available until</p>
          {ad.expDate}
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = (state, ownProps) => {
  return {
    ad: state.advertisements.ads.find(ad => ad._id === ownProps.match.params.id)
  }
}

export default connect(mapStateToProps)(AdPage)
