import React from 'react'
import { connect } from 'react-redux'
import { modifyFilter, modifyFilterRange } from '../../redux/actions/view'
import { fetchAdverstisements } from '../../redux/actions'
import Field from '../Sort/Field'
import Range from './Range'

class FilterBox extends React.Component {
  constructor(props) {
    super(props)
    this.onFieldChosen = this.onFieldChosen.bind(this)
    this.onRangeChange = this.onRangeChange.bind(this)
    this.state = {
      field: null,
      from: undefined,
      to: undefined
    }
  }

  onFieldChosen(field) {
    this.setState({
      field
    })
  }

  onRangeChange(range) {
    this.setState(range)
  }

  onApply() {

  }

  render() {
    return (
      <div className="card">
        <div className="card-header">
          <h1 className="card-header-title">Filter</h1>
        </div>
        <div className="card-content">
          <Field onFieldChosen={this.onFieldChosen}/>
          <Range onRangeChange={this.onRangeChange}/>
          <button className="button is-info is-rounded">Apply</button>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  filter: state.view.filter,
  from: state.view.from,
  to: state.view.to
})

export default connect(mapStateToProps, { modifyFilter, modifyFilterRange })(FilterBox)
