import service from '../../sevices/dataService'
import { getOwnAds } from '../../redux/actions/index'
import { connect } from 'react-redux'
import React from 'react'

const Advertisement = ({ ad }) => {
  function onDelete() {
    if (confirm('Do you want to delete this item ?')) {
      service.deleteAd(ad._id)
             .then(data => alert(data))
             .catch(error => alert('Please try again'))
    }
  }

  return (
    <div className="card card-content">
      { ad.title }
      <button className="button is-danger is-rounded is-pulled-right"
              onClick={onDelete}>
        Delete
      </button>
    </div>
  )
}

export default Advertisement
