import { MODIFY_SORT } from '../constants/action-types'

const initialState = {
  sort: 'price',
  order: 'asc'
}

export default function viewReducer(state = initialState, action) {
  switch(action.type) {
    case MODIFY_SORT:
      return {
        ...state,
        sort: action.payload.sort
      }
    default:
      return state
  }
}
