import axios from 'axios'
import auth from '../sevices/authService'
import history from '../router/history'

function login(username, password) {
  return new Promise((resolve, reject) => {
    axios.post('http://localhost:8080/authenticate', {
      name: username,
      password
    }, {
      headers: {
        "Content-Type": "application/json"
      }
    })
    .then(resp => resp.data)
    .then(data => {
      auth.login(data.data.accessToken)
      return data
    })
    .then(data => resolve(data))
    .catch(error => handleResponse(error, reject))
  })
}

function register(username, password) {
  return new Promise((resolve, reject) => {
    axios.post('http://localhost:8080/users', {
      name: username,
      password
    }, {
      headers: {
        "Content-Type": "application/json"
      }
    })
    .then(resp => resp.data)
    .then(data => {
      auth.login(data.data.accessToken)
      return data
    })
    .then(data => resolve(data))
    .catch(error => { 
      reject(error) 
    })
  })
}


//Advertisement
function getAllAdvertisements(page, sort) {
  return new Promise((resolve, reject) => {
    axios.get('http://localhost:8080/advertisements', {
      params: { 
        p: page,
        order: sort.order,
        field: sort.field
      }
    })
         .then(resp => resp.data)
         .then(data => resolve(data))
         .catch(error => reject(error))
  })
}

function getOwnAdvertisement(page) {
  return new Promise((resolve, reject) => {
    axios.get('http://localhost:8080/advertisements/me', {
      params: { p: page },
      headers: auth.getAuthHeader()
    })
    .then(resp => resolve(resp.data))
    .catch(error => reject(error))
  })
}

function getOneAdvertisement(id) {
  return new Promise((resolve, reject) => {
    axios.get(`http://localhost:8080/advertisements/${id}`, {
      headers: auth.getAuthHeader()
    })
    .then(resp => resolve(resp.data))
    .catch(error => handleResponse(error, reject))
  })
}

function deleteAd(_id) {
  return new Promise((resolve, reject) => {
    axios.delete('http://localhost:8080/advertisements', {
      headers: auth.getAuthHeader(),
      data: {
        _id
      }
    })
    .then(data => resolve(data.message))
    .catch(error => reject(error))
  })
}

function addAdvertisement(advertisement) {
  return new Promise((resolve, reject) => {
    axios.post('http://localhost:8080/advertisements', advertisement, {
      headers: {
        'Content-Type': 'application/json',
        ...auth.getAuthHeader()
      }
    })
    .then(resp => resolve(resp.data))
    .catch(error => reject(error))
  })
}

function updateAdvertisement(advertisement) {
  return new Promise((resolve, reject) => {
    axios.put('http://localhost:8080/advertisements', advertisement, {
      headers: {
        'Content-Type': 'application/json',
        ...auth.getAuthHeader()
      }
    })
    .then(resp => resolve(resp.data))
    .catch(error => reject(error))
  })
}

//User
function getUserInfo() {
  return new Promise((resolve, reject) => {
    axios.get('http://localhost:8080/users/me', {
      headers: auth.getAuthHeader()
    })
    .then(resp => { 
      resolve(resp.data) 
    })
    .catch(error => handleResponse(error, reject))
  })
}

function logout() {
  auth.logout()
  history.push('/login')
}

//Projects
function addProject(project) {
  return new Promise((resolve, reject) => {
    axios.post('http://localhost:8080/projects', project, {
    headers: {
      "Content-Type": "application/json",
      ...auth.getAuthHeader()
    }
  })
  .then(resp => resolve(resp.data))
  .catch(error => handleResponse(error, reject))
  })
}

function fetchOwnProjects() {
  return new Promise((resolve, reject) => {
    axios.get("http://localhost:8080/projects/me", {
      headers: auth.getAuthHeader()
    })
    .then(resp => resolve(resp.data))
    .catch(error => handleResponse(error, reject))
  })
}

function addAdToProject(projectId, adId) {
  return new Promise((resolve, reject) => {
    axios.post(`http://localhost:8080/projects/${projectId}/addAdvertisement`, {
      adId
    }, 
    {
      headers: {
        'Content-Type': 'application/json',
        ...auth.getAuthHeader()
      }
    })
    .then(resp => resolve(resp.data))
    .catch(error => handleResponse(error, reject))
  })
}

function removeAdFromProject(projectId, adId) {
  return new Promise((resolve, reject) => {
    axios.delete(`http://localhost:8080/projects/${projectId}/removeAdvertisement`, {
      headers: {
        'Content-Type': 'application/json',
        ...auth.getAuthHeader()
      },
      data: {
        adId
      }
    })
    .then(resp => resolve(resp.data))
    .catch(error => handleResponse(error, reject))
  })
}

function handleResponse(error, reject) {
  const { status } = error.response
  if (status === 302) {
    logout()
  } else {
    reject(error)
  }
}

export default {
  login,
  logout,
  register,
  getAllAdvertisements,
  getOwnAdvertisement,
  getOneAdvertisement,
  deleteAd,
  addAdvertisement,
  removeAdFromProject,
  updateAdvertisement,
  addAdToProject,
  fetchOwnProjects,
  addProject,
  getUserInfo
}
