import {
  LOGIN_START,
  LOGIN_FAILED,
  LOGIN_SUCCESS,
  LOGOUT
} from '../constants/action-types'
import service from '../../sevices/dataService'
import history from '../../router/history'

function loginStart() {
  return {
    type: LOGIN_START
  }
}

function loginFailed() {
  return {
    type: LOGIN_FAILED
  }
}

function loginSuccess(payload) {
  return {
    type: LOGIN_SUCCESS,
    payload
  }
}

export function logout() {
  function _logout() {
    return {
      type: LOGOUT
    }
  }

  return function(dispatch) {
    service.logout()
    dispatch(_logout())
  }
}

export function login(username, password) {
  return function(dispatch) {
    dispatch(loginStart())
    service.login(username, password)
           .then(success => { 
              console.log(success)
              dispatch(loginSuccess({ accessToken: success.data.accessToken })) 
              history.push('/')
            })
           .catch(error => dispatch(loginFailed({ error })))
  }
}
